sys_script_vtable.script:
(function executeQuery(v_table, v_query) {
    var url = 'https://domainteam.service-now.com';
    var tableAPI = '/api/now/table';
    var table = '/problem';
    var endpoint = url + tableAPI + table;
    var request = new sn_ws.RESTMessageV2();
    request.setEndpoint(endpoint);
    request.setHttpMethod('GET');
    request.setAuthenticationProfile("basic", "1654da7fdb997300a261f9e139961973");
    request.setRequestHeader("Accept", "application/json");
    request.setQueryParameter('sysparm_query', 'active=true');
    request.setQueryParameter('sysparm_display_value', 'all');
    request.setQueryParameter('sysparm_fields', 'number,short_description,priority,assigned_to,sys_updated_on,company,assignment_group,sys_id');

    var response = request.execute();
    var responseCode = response.getStatusCode();
    var responseBody = response.getBody();
    var json = new global.JSON();
    var result = json.decode(responseBody).result;

    var i;
    var rec = {};
    var row;
    for (i in result) {
        row = result[i];
        rec.assigned_to = row.assigned_to.display_value;
        rec.assignment_group = row.assignment_group.display_value;
        rec.priority = row.priority.display_value;
        rec.sys_id = row.sys_id.display_value;
        rec.short_description = row.short_description.display_value;
        rec.number = row.number.display_value;
		rec.company = row.company.display_value;
		rec.updated = row.sys_updated_on.value;
        rec.endpoint = url + table + '.do?sys_id=' + rec.sys_id;
        v_table.addRow(rec);
    }
})(v_table, v_query);
